const link=document.querySelector(".hotel-search-button"),popup=document.querySelector(".modal-search"),form=document.querySelector(".search-form"),arrival=popup.querySelector("[name=start-date]"),departure=popup.querySelector("[name=end-date]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))}),form.addEventListener("submit",function(e){arrival.value&&departure.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});
